import React, { useEffect, useState } from 'react'
import './PlayVideo.css'
import video1 from '../../assets/video.mp4'
import like from '../../assets/like.png'
import dislike from '../../assets/dislike.png'
import share from '../../assets/share.png'
import save from '../../assets/save.png'
import jack from '../../assets/jack.png'
import user_profile from '../../assets/user_profile.jpg'
import { API_KEY, value_converter } from '../../data'
import moment from 'moment';
import { useParams } from 'react-router-dom'

export const PlayVideo = ({}) => {

  const {videoId} = useParams();

  const [apiData,setApiData] = useState(null);
  const [channelData,setChannelData] = useState(null);
  const [commentData,setCommentData] = useState([]);
  const fetchVideoData = async ()=>{
        
    const videoDetails_url = `https://youtube.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&id=${videoId}&key=${API_KEY}`
    await fetch(videoDetails_url).then(res=>res.json()).then(data=>setApiData(data.items[0]))

  }

  const fetchOtherData = async () => {
    if (!apiData || !apiData.snippet?.channelId) return;

    const channelDataUrl = `https://youtube.googleapis.com/youtube/v3/channels?part=snippet%2CcontentDetails%2Cstatistics&id=${apiData.snippet.channelId}&key=${API_KEY}`;
    fetch(channelDataUrl)
      .then((res) => res.json())
      .then((data) => setChannelData(data.items ? data.items[0] : null))
      .catch((error) => console.error('Error fetching channel data:', error));

    const commentUrl = `https://youtube.googleapis.com/youtube/v3/commentThreads?part=snippet%2Creplies&maxResults=50&videoId=${videoId}&key=${API_KEY}`;
    fetch(commentUrl)
      .then((res) => res.json())
      .then((data) => setCommentData(data.items || []))
      .catch((error) => console.error('Error fetching comment data:', error));
  };

  useEffect(()=>{
     fetchVideoData();
  },[videoId])

  useEffect(()=>{
    fetchOtherData();
  },[apiData])

  return (
    <div className='play-video'>
       {/* <video src={video1} controls autoPlay muted></video> */}
       <iframe src={`https://www.youtube.com/embed/${videoId}?autoplay=1`}  frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen ></iframe>
       <h3>{apiData?apiData.snippet.title:"Title Here"}</h3>

       <div className="play-video-info">
         <p>{apiData?value_converter(apiData.statistics.viewCount):"16k "} views &bull; {apiData?moment(apiData.snippet.publishedAt).fromNow():""}</p>
         <div>
            <span><img src={like} alt="" />{apiData?value_converter(apiData.statistics.likeCount):155}</span>
            <span><img src={dislike} alt="" /></span>
            <span><img src={share} alt="" />share</span>
            <span><img src={save} alt="" />Save</span>
         </div>
       </div>
         <hr />
         <div className='publisher'>
            <img src={channelData?channelData.snippet.thumbnails.default.url:""} alt="" />
            <div>
                <p>{apiData?apiData.snippet.channelTitle:""}</p>
                <span>{channelData?(channelData.statistics.subscriberCount):"1M"} Subscribers</span>
            </div>
            <button>Subscribe</button>
         </div>
      
       <div className="vid-description">
           <p>{apiData?apiData.snippet.description.slice(0,250):"Description Here"}</p>
          <hr />
          <h4>{apiData?value_converter(apiData.statistics.commentCount):"102"} comments</h4>
          {commentData.map((item,index)=>{
              return (
                <div key={index} className='comment'>
                <img src={item.snippet.topLevelComment.snippet.authorProfileImageUrl} alt="" />
                <div>
                    <h3>{item.snippet.topLevelComment.snippet.authorDisplayName}<span>{moment(item.snippet.topLevelComment.snippet.publishedAt).fromNow()}</span></h3>
                    <p>{item.snippet.topLevelComment.snippet.textDisplay}</p>
                    <div className='comment-action'>
                       <img src={like} alt="" />
                       <span>{value_converter(item.snippet.topLevelComment.snippet.likeCount)}</span>
                       <img src={dislike} alt="" /> 
                    </div>
                </div>
              </div>
              )
          })}
          
       </div>
    </div>
  )
}
