import React, { useEffect, useState } from 'react';
import './Recommended.css';
import { API_KEY } from '../../data';

export const Recommended = ({ categoryId }) => {
  const [apiData, setApiData] = useState([]);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    const relatedVideoUrl = `https://youtube.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&chart=mostPopular&regionCode=US&videoCategoryId=${categoryId}&key=${API_KEY}`;
    fetch(relatedVideoUrl)
      .then((res) => res.json())
      .then((data) => {
        if (data.items) {
          setApiData(data.items);
        } else {
          setError('No videos found.');
        }
      })
      .catch((err) => setError('Failed to fetch data. Please try again.'));
  };

  useEffect(() => {
    fetchData();
  }, [categoryId]); // Include `categoryId` as a dependency.

  return (
    <div className="recommended">
      {error ? (
        <p className="error">{error}</p> // Display error message if any.
      ) : (
        apiData.map((video, index) => (
          <div key={index} className="side-video-list">
            <img
              src={video.snippet?.thumbnails?.medium?.url || ''}
              alt={video.snippet?.title || 'Video thumbnail'}
            />
            <div className="vid-info">
              <h4>{video.snippet?.title || 'Video title'}</h4>
              <p>{video.snippet?.channelTitle || 'Channel name'}</p>
              <p>{video.statistics?.viewCount || '0'} Views</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
};
